import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import "../styles/Home.css";

const Home = () => {
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedGenre, setSelectedGenre] = useState("All Movies");
    const [currentSlide, setCurrentSlide] = useState(0);
    const navigate = useNavigate();

    const genres = ["All Movies", "Action", "Sci-Fi", "Drama", "Imax", "Animation"];

    const featuredMovies = [
        {
            title: "INTERSTELLAR",
            badge: "TRENDING NOW",
            description: "A team of explorers travel through a wormhole in space in an attempt to ensure humanity's survival.",
            image: "" // Placeholder for user to add
        },
        {
            title: "DUNE: PART TWO",
            badge: "NEW RELEASE",
            description: "The saga continues as Paul Atreides unites with Chani and the Fremen.",
            image: "" // Placeholder for user to add
        }
    ];

    useEffect(() => {
        fetchEvents();
        const interval = setInterval(() => {
            setCurrentSlide((prev) => (prev + 1) % featuredMovies.length);
        }, 5000);
        return () => clearInterval(interval);
    }, []);

    const fetchEvents = () => {
        setLoading(true);
        API.get("/events")
            .then((res) => {
                setEvents(res.data);
                setError(null);
            })
            .catch((err) => {
                console.error("Error fetching events:", err);
                setError(err.message);
            })
            .finally(() => setLoading(false));
    };

    const filteredEvents = events.filter((event) => {
        const matchesSearch = event.name.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesGenre = selectedGenre === "All Movies" || event.genre === selectedGenre;
        return matchesSearch && matchesGenre;
    });

    const handleBookTickets = (eventId) => {
        navigate(`/events`);
    };

    return (
        <div className="home-container">
            {/* Sidebar Navigation */}
            <aside className="sidebar">
                <div className="logo">
                    <div className="logo-icon">üé¨</div>
                </div>
                <nav className="sidebar-nav">
                    <a href="#home" className="nav-item active">
                        <span className="nav-icon">üè†</span>
                        <span className="nav-label">HOME</span>
                    </a>
                    <a href="#tickets" className="nav-item">
                        <span className="nav-icon">üé´</span>
                        <span className="nav-label">TICKETS</span>
                    </a>
                    <a href="#movies" className="nav-item">
                        <span className="nav-icon">üé¨</span>
                        <span className="nav-label">MOVIES</span>
                    </a>
                    <a href="#offers" className="nav-item">
                        <span className="nav-icon">üéÅ</span>
                        <span className="nav-label">OFFERS</span>
                    </a>
                </nav>
                <div className="sidebar-settings">
                    <button className="settings-btn">‚öôÔ∏è</button>
                </div>
            </aside>

            {/* Main Content */}
            <main className="main-content">
                {/* Header */}
                <header className="home-header">
                    <div className="brand">
                        <span className="brand-name">CINE</span>
                        <span className="brand-gold">GOLD</span>
                    </div>
                    <div className="search-bar">
                        <span className="search-icon">üîç</span>
                        <input
                            type="text"
                            placeholder="Search movies, actors, directors..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                    </div>
                    <div className="user-profile">
                        <img src="" alt="User" className="profile-avatar" />
                    </div>
                </header>

                {/* Hero Carousel */}
                <section className="hero-carousel">
                    <div className="carousel-slides">
                        {featuredMovies.map((movie, index) => (
                            <div
                                key={index}
                                className={`carousel-slide ${index === currentSlide ? "active" : ""}`}
                                style={{ backgroundImage: movie.image ? `url(${movie.image})` : 'none' }}
                            >
                                <div className="slide-overlay"></div>
                                <div className="slide-content">
                                    <span className="movie-badge">{movie.badge}</span>
                                    <h1 className="movie-title">{movie.title}</h1>
                                    <p className="movie-description">{movie.description}</p>
                                    <div className="slide-actions">
                                        <button className="btn-book" onClick={() => handleBookTickets()}>
                                            ‚ñ∂ Book Tickets
                                        </button>
                                        <button className="btn-trailer">Trailer</button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="carousel-indicators">
                        {featuredMovies.map((_, index) => (
                            <button
                                key={index}
                                className={`indicator ${index === currentSlide ? "active" : ""}`}
                                onClick={() => setCurrentSlide(index)}
                            />
                        ))}
                    </div>
                </section>

                {/* Genre Filter */}
                <div className="genre-filter">
                    {genres.map((genre) => (
                        <button
                            key={genre}
                            className={`genre-btn ${selectedGenre === genre ? "active" : ""}`}
                            onClick={() => setSelectedGenre(genre)}
                        >
                            {genre}
                        </button>
                    ))}
                </div>

                {/* Now Showing Section */}
                <section className="now-showing">
                    <div className="section-header">
                        <h2>Now Showing</h2>
                        <button className="view-all-btn">View All</button>
                    </div>

                    {loading && <div className="loading-text">‚è≥ Loading movies...</div>}
                    {error && <div className="error-text">‚ùå Error: {error}</div>}

                    <div className="movies-grid">
                        {filteredEvents.map((event) => (
                            <div key={event._id} className="movie-card">
                                <div className="movie-poster">
                                    {/* Placeholder for movie poster */}
                                    <div className="poster-placeholder">üé¨</div>
                                    <div className="movie-rating">
                                        <span className="star">‚≠ê</span>
                                        <span className="rating-value">8.8</span>
                                    </div>
                                </div>
                                <div className="movie-info">
                                    <h3 className="movie-name">{event.name}</h3>
                                    <p className="movie-meta">
                                        <span>{event.genre || "Drama"}</span>
                                        <span> ‚Ä¢ </span>
                                        <span>{event.duration || "2h 48m"}</span>
                                    </p>
                                </div>
                            </div>
                        ))}
                    </div>

                    {filteredEvents.length === 0 && !loading && (
                        <div className="no-movies">üì≠ No movies found matching your criteria.</div>
                    )}
                </section>
            </main>
        </div>
    );
};

export default Home;
