import React, { useState } from "react";
import API from "../services/api";
import "../styles/Auth.css";

const Login = () => {
  const [form, setForm] = useState({
    email: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");
    
    try {
      const res = await API.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      setMessage("‚úÖ Login successful!");
      setIsLoggedIn(true);
      setForm({ email: "", password: "" });
    } catch (err) {
      const errorMsg = err.response?.data?.message || "Login failed";
      setError("‚ùå " + errorMsg);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setForm({ email: "", password: "" });
    setMessage("");
    setError("");
  };

  if (isLoggedIn) {
    return (
      <div className="auth-card">
        <h2>‚úÖ Logged In</h2>
        <p style={{ color: "#27ae60", textAlign: "center", fontSize: "1.1rem", marginBottom: "20px" }}>
          You are now logged in! Proceed to book your tickets below.
        </p>
        <button type="button" className="submit-btn" onClick={handleLogout} style={{ background: "#e74c3c" }}>
          Logout
        </button>
      </div>
    );
  }

  return (
    <div className="auth-card">
      <h2>üîê Login</h2>
      
      {message && <div className="success-message">{message}</div>}
      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email</label>
          <input
            name="email"
            type="email"
            placeholder="your@email.com"
            value={form.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            name="password"
            type="password"
            placeholder="Your password"
            value={form.password}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="submit-btn">Login</button>
      </form>
    </div>
  );
};

export default Login;
