import React, { useState } from "react";
import API from "../services/api";
import loadRazorpay from "../services/loadRazorpay";

const EventCard = ({ event }) => {
  const [seats, setSeats] = useState(1);

  const handleBooking = async () => {
    const res = await loadRazorpay();
    if (!res) {
      alert("Razorpay SDK failed to load");
      return;
    }

    try {
      // 1Ô∏è‚É£ Create order from backend
      const orderRes = await API.post("/payment/create-order", {
        eventId: event._id,
        seatsBooked: seats
      });

      const { id: order_id, amount, currency } = orderRes.data;

      // 2Ô∏è‚É£ Open Razorpay checkout
      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount,
        currency,
        name: "Ticket Booking",
        description: event.title,
        order_id,

        handler: async function (response) {
          // 3Ô∏è‚É£ Verify payment on backend
          await API.post("/payment/verify", {
            razorpay_order_id: response.razorpay_order_id,
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_signature: response.razorpay_signature,
            eventId: event._id,
            seatsBooked: seats
          });

          alert("Booking confirmed üéâ");
        },

        theme: {
          color: "#3399cc"
        }
      };

      const paymentObject = new window.Razorpay(options);
      paymentObject.open();

    } catch (error) {
      alert(error.response?.data?.message || "Payment failed");
    }
  };

  return (
    <div style={{ border: "1px solid #ccc", padding: "10px", margin: "10px" }}>
      <h3>{event.title}</h3>
      <p>{event.description}</p>
      <p>‚Çπ{event.price}</p>
      <p>Seats Left: {event.availableSeats}</p>

      <input
        type="number"
        min="1"
        max={event.availableSeats}
        value={seats}
        onChange={(e) => setSeats(Number(e.target.value))}
      />

      <button onClick={handleBooking}>Book Ticket</button>
    </div>
  );
};

export default EventCard;
