import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import "../styles/Auth.css";

const Register = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");
    setIsLoading(true);
    
    try {
      await API.post("/auth/register", form);
      setMessage("‚úÖ Registration successful! Redirecting to login...");
      setForm({ name: "", email: "", password: "" });
      setTimeout(() => navigate("/login"), 2000);
    } catch (err) {
      const errorMsg = err.response?.data?.message || "Registration failed";
      setError("‚ùå " + errorMsg);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-hero">
        <div className="hero-badge">CINEGOLD</div>
        <h1 className="hero-title">Join The <span className="gold-text">Cinema Experience</span></h1>
        <p className="hero-subtitle">Create your account and start booking premium movie tickets today</p>
        <div className="hero-features">
          <div className="feature">
            <span className="feature-icon">üé¨</span>
            <p>Exclusive Access</p>
          </div>
          <div className="feature">
            <span className="feature-icon">üèÜ</span>
            <p>Earn Rewards</p>
          </div>
          <div className="feature">
            <span className="feature-icon">üíé</span>
            <p>Premium Benefits</p>
          </div>
        </div>
      </div>

      <div className="auth-form-wrapper">
        <div className="auth-card">
          <div className="form-header">
            <h2>Create Account</h2>
            <p className="form-subtitle">Sign up to get started</p>
          </div>
          
          {message && <div className="success-message">{message}</div>}
          {error && <div className="error-message">{error}</div>}

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Full Name</label>
              <input
                name="name"
                placeholder="John Doe"
                value={form.name}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>

            <div className="form-group">
              <label>Email Address</label>
              <input
                name="email"
                type="email"
                placeholder="your@email.com"
                value={form.email}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                name="password"
                type="password"
                placeholder="Create a strong password"
                value={form.password}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>

            <button type="submit" className="submit-btn" disabled={isLoading}>
              {isLoading ? "Creating Account..." : "Register Now"}
            </button>
          </form>

          <div className="form-footer">
            <p>Already have an account? <a href="/login" className="form-link">Login here</a></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
