import React, { useEffect, useState } from "react";
import "../styles/Auth.css";

const DebugPage = () => {
    const [debugInfo, setDebugInfo] = useState({});

    useEffect(() => {
        const token = localStorage.getItem("token");
        const userStr = localStorage.getItem("user");
        const user = userStr ? JSON.parse(userStr) : null;

        setDebugInfo({
            hasToken: !!token,
            token: token ? token.substring(0, 20) + "..." : "No token",
            hasUser: !!user,
            user: user,
            isAdmin: user?.isAdmin,
        });
    }, []);

    return (
        <div className="auth-card" style={{ maxWidth: "800px", margin: "50px auto" }}>
            <h2>üîç Debug Information</h2>

            <div style={{ textAlign: "left", marginTop: "20px" }}>
                <h3>LocalStorage Data:</h3>
                <pre style={{
                    background: "#f5f5f5",
                    padding: "15px",
                    borderRadius: "8px",
                    overflow: "auto"
                }}>
                    {JSON.stringify(debugInfo, null, 2)}
                </pre>

                <h3 style={{ marginTop: "30px" }}>What should happen:</h3>
                <ul style={{ lineHeight: "1.8" }}>
                    <li>‚úÖ hasToken: true</li>
                    <li>‚úÖ hasUser: true</li>
                    <li>‚úÖ user.isAdmin: true (for admin users)</li>
                </ul>

                <h3 style={{ marginTop: "30px" }}>If isAdmin is false or undefined:</h3>
                <ol style={{ lineHeight: "1.8" }}>
                    <li>Run: <code>node checkAdmin.js</code> in backend folder</li>
                    <li>Verify your user has isAdmin: true in database</li>
                    <li>Logout completely</li>
                    <li>Login again</li>
                    <li>Check this page again</li>
                </ol>

                <div style={{ marginTop: "30px", padding: "15px", background: "#fff3cd", borderRadius: "8px" }}>
                    <strong>‚ö†Ô∏è Important:</strong> The admin status is set when you LOGIN.
                    If you were already logged in when we made you admin, you need to logout and login again.
                </div>
            </div>
        </div>
    );
};

export default DebugPage;
