import React, { useEffect, useState } from "react";
import API from "../services/api";
import EventCard from "../components/EventCard";
import "../styles/Events.css";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    API.get("/events")
      .then((res) => {
        console.log("Events fetched:", res.data);
        setEvents(res.data);
        setError(null);
      })
      .catch((err) => {
        console.error("Error fetching events:", err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div className="loading-text">‚è≥ Loading events...</div>;
  if (error) return <div className="error-text">‚ùå Error: {error}</div>;
  if (events.length === 0) return <div className="no-events">üì≠ No events available. Check your backend!</div>;

  return (
    <div className="events-container">
      <h2>üé¨ Available Events</h2>
      <div className="events-grid">
        {events.map((event) => (
          <EventCard key={event._id} event={event} />
        ))}
      </div>
    </div>
  );
};

export default Events;
