import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import API from "../services/api";
import "../styles/Auth.css";

const Login = ({ setIsLoggedIn, setIsAdmin }) => {
  const [form, setForm] = useState({
    email: "",
    password: ""
  });
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // If already logged in, redirect to events
    if (localStorage.getItem("token")) {
      navigate("/events");
    }
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");

    try {
      const res = await API.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      setMessage("‚úÖ Login successful!");
      setIsLoggedIn(true);
      setIsAdmin(res.data.user.isAdmin || false);
      setForm({ email: "", password: "" });
      setTimeout(() => navigate("/events"), 1500);
    } catch (err) {
      const errorMsg = err.response?.data?.message || "Login failed";
      setError("‚ùå " + errorMsg);
    }
  };

  const handleSocialLogin = (provider) => {
    // Placeholder for social login functionality
    alert(`${provider} login will be implemented in the future`);
  };

  return (
    <div className="auth-container">
      <div className="auth-background">
        {/* Background image will be added here manually */}
      </div>

      <div className="auth-card">
        <div className="auth-header">
          <h1>Sign In</h1>
          <p className="auth-subtitle">Access your premium cinema experience</p>
        </div>

        {message && <div className="success-message">{message}</div>}
        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Email Address</label>
            <input
              name="email"
              type="email"
              placeholder="e.g. alexander0@executive.com"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <div className="password-header">
              <label>Password</label>
              <Link to="/forgot-password" className="forgot-password">
                Forgot Password?
              </Link>
            </div>
            <div className="password-input-wrapper">
              <input
                name="password"
                type={showPassword ? "text" : "password"}
                placeholder="Enter your password"
                value={form.password}
                onChange={handleChange}
                required
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
                aria-label="Toggle password visibility"
              >
                {showPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
              </button>
            </div>
          </div>

          <button type="submit" className="submit-btn">Sign In</button>
        </form>

        <div className="divider">
          <span>OR CONTINUE WITH</span>
        </div>

        <div className="social-login">
          <button
            type="button"
            className="social-btn google-btn"
            onClick={() => handleSocialLogin('Google')}
          >
            <span className="social-icon">G</span>
            Google
          </button>
          <button
            type="button"
            className="social-btn apple-btn"
            onClick={() => handleSocialLogin('Apple')}
          >
            <span className="social-icon"></span>
            Apple
          </button>
        </div>

        <p className="signup-link">
          Don't have an account? <Link to="/register">Join the Club</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
