import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import "../styles/AdminPanel.css";

const AdminPanel = () => {
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [editingEvent, setEditingEvent] = useState(null);
    const [formData, setFormData] = useState({
        title: "",
        description: "",
        date: "",
        price: "",
        totalSeats: "",
        genre: "",
        duration: "",
        imageUrl: "",
        rating: ""
    });
    const navigate = useNavigate();

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            setLoading(true);
            const res = await API.get("/events");
            setEvents(res.data);
        } catch (error) {
            console.error("Error fetching events:", error);
            if (error.response?.status === 403 || error.response?.status === 401) {
                alert("Access denied. Admin privileges required.");
                navigate("/events");
            }
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            if (editingEvent) {
                // Update existing event
                await API.put(`/events/${editingEvent._id}`, formData);
                alert("Event updated successfully!");
            } else {
                // Create new event
                await API.post("/events", formData);
                alert("Event created successfully!");
            }

            // Reset form
            setFormData({
                title: "",
                description: "",
                date: "",
                price: "",
                totalSeats: "",
                genre: "",
                duration: "",
                imageUrl: "",
                rating: ""
            });
            setShowForm(false);
            setEditingEvent(null);
            fetchEvents();
        } catch (error) {
            console.error("Error saving event:", error);
            alert(error.response?.data?.message || "Error saving event");
        }
    };

    const handleEdit = (event) => {
        setEditingEvent(event);
        setFormData({
            title: event.title,
            description: event.description,
            date: event.date.split("T")[0],
            price: event.price,
            totalSeats: event.totalSeats,
            genre: event.genre || "",
            duration: event.duration || "",
            imageUrl: event.imageUrl || "",
            rating: event.rating || ""
        });
        setShowForm(true);
    };

    const handleDelete = async (eventId) => {
        if (!window.confirm("Are you sure you want to delete this event?")) {
            return;
        }

        try {
            await API.delete(`/events/${eventId}`);
            alert("Event deleted successfully!");
            fetchEvents();
        } catch (error) {
            console.error("Error deleting event:", error);
            alert(error.response?.data?.message || "Error deleting event");
        }
    };

    const handleCancel = () => {
        setShowForm(false);
        setEditingEvent(null);
        setFormData({
            title: "",
            description: "",
            date: "",
            price: "",
            totalSeats: "",
            genre: "",
            duration: "",
            imageUrl: "",
            rating: ""
        });
    };

    if (loading) {
        return <div className="admin-loading">‚è≥ Loading admin panel...</div>;
    }

    return (
        <div className="admin-panel">
            <div className="admin-header">
                <h1>üé¨ Admin Panel</h1>
                <button
                    className="add-event-btn"
                    onClick={() => setShowForm(!showForm)}
                >
                    {showForm ? "‚ùå Cancel" : "‚ûï Add New Event"}
                </button>
            </div>

            {showForm && (
                <div className="event-form-container">
                    <h2>{editingEvent ? "‚úèÔ∏è Edit Event" : "‚ûï Create New Event"}</h2>
                    <form onSubmit={handleSubmit} className="event-form">
                        <div className="form-row">
                            <div className="form-group">
                                <label>Title *</label>
                                <input
                                    type="text"
                                    name="title"
                                    value={formData.title}
                                    onChange={handleInputChange}
                                    required
                                    placeholder="Enter event/movie title"
                                />
                            </div>

                            <div className="form-group">
                                <label>Genre</label>
                                <input
                                    type="text"
                                    name="genre"
                                    value={formData.genre}
                                    onChange={handleInputChange}
                                    placeholder="e.g., Action, Comedy, Drama"
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Description *</label>
                            <textarea
                                name="description"
                                value={formData.description}
                                onChange={handleInputChange}
                                required
                                rows="4"
                                placeholder="Enter event description"
                            />
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>Date *</label>
                                <input
                                    type="date"
                                    name="date"
                                    value={formData.date}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>Duration</label>
                                <input
                                    type="text"
                                    name="duration"
                                    value={formData.duration}
                                    onChange={handleInputChange}
                                    placeholder="e.g., 2h 30m"
                                />
                            </div>

                            <div className="form-group">
                                <label>Rating</label>
                                <input
                                    type="text"
                                    name="rating"
                                    value={formData.rating}
                                    onChange={handleInputChange}
                                    placeholder="e.g., PG-13, R, 8.5/10"
                                />
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>Price ($) *</label>
                                <input
                                    type="number"
                                    name="price"
                                    value={formData.price}
                                    onChange={handleInputChange}
                                    required
                                    min="0"
                                    step="0.01"
                                    placeholder="0.00"
                                />
                            </div>

                            <div className="form-group">
                                <label>Total Seats *</label>
                                <input
                                    type="number"
                                    name="totalSeats"
                                    value={formData.totalSeats}
                                    onChange={handleInputChange}
                                    required
                                    min="1"
                                    placeholder="100"
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Image URL</label>
                            <input
                                type="url"
                                name="imageUrl"
                                value={formData.imageUrl}
                                onChange={handleInputChange}
                                placeholder="https://example.com/image.jpg"
                            />
                        </div>

                        <div className="form-actions">
                            <button type="submit" className="submit-btn">
                                {editingEvent ? "üíæ Update Event" : "‚ûï Create Event"}
                            </button>
                            <button type="button" onClick={handleCancel} className="cancel-btn">
                                ‚ùå Cancel
                            </button>
                        </div>
                    </form>
                </div>
            )}

            <div className="events-section">
                <h2>üìã All Events ({events.length})</h2>
                {events.length === 0 ? (
                    <div className="no-events">
                        <p>üì≠ No events found. Create your first event!</p>
                    </div>
                ) : (
                    <div className="events-table-container">
                        <table className="events-table">
                            <thead>
                                <tr>
                                    <th>Title</th>
                                    <th>Genre</th>
                                    <th>Date</th>
                                    <th>Price</th>
                                    <th>Seats</th>
                                    <th>Duration</th>
                                    <th>Rating</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {events.map((event) => (
                                    <tr key={event._id}>
                                        <td className="event-title">{event.title}</td>
                                        <td>{event.genre || "N/A"}</td>
                                        <td>{new Date(event.date).toLocaleDateString()}</td>
                                        <td>${event.price}</td>
                                        <td>
                                            {event.availableSeats}/{event.totalSeats}
                                        </td>
                                        <td>{event.duration || "N/A"}</td>
                                        <td>{event.rating || "N/A"}</td>
                                        <td className="action-buttons">
                                            <button
                                                onClick={() => handleEdit(event)}
                                                className="edit-btn"
                                                title="Edit event"
                                            >
                                                ‚úèÔ∏è
                                            </button>
                                            <button
                                                onClick={() => handleDelete(event._id)}
                                                className="delete-btn"
                                                title="Delete event"
                                            >
                                                üóëÔ∏è
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>
        </div>
    );
};

export default AdminPanel;
