import React, { useEffect, useState } from "react";
import API from "../services/api";
import EventCard from "../components/EventCard";
import "../styles/Events.css";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedGenre, setSelectedGenre] = useState("All Movies");
  const [currentSlide, setCurrentSlide] = useState(0);

  const genres = ["All Movies", "Action", "Sci-Fi", "Drama", "Thriller"];

  const fetchEvents = () => {
    setLoading(true);
    API.get("/events")
      .then((res) => {
        console.log("Events fetched:", res.data);
        setEvents(res.data);
        setError(null);
      })
      .catch((err) => {
        console.error("Error fetching events:", err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchEvents();
    // Auto-rotate hero carousel
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % Math.max(events.length, 1));
    }, 5000);
    return () => clearInterval(interval);
  }, [events.length]);

  if (loading) return <div className="loading-text">‚è≥ Loading events...</div>;
  if (error) return <div className="error-text">‚ùå Error: {error}</div>;

  const featuredEvent = events.length > 0 ? events[currentSlide % events.length] : null;

  return (
    <div className="events-page">
      {/* HERO CAROUSEL */}
      {featuredEvent && (
        <section className="hero-carousel">
          <div className="hero-slide">
            {/* Background Image */}
            {featuredEvent.imageUrl && (
              <img
                src={featuredEvent.imageUrl}
                alt={featuredEvent.title}
                className="hero-bg"
              />
            )}
            {!featuredEvent.imageUrl && (
              <div className="hero-bg-placeholder">
                <span>üé¨</span>
              </div>
            )}

            {/* Overlay */}
            <div className="hero-overlay"></div>

            {/* Hero Content */}
            <div className="hero-content">
              <div className="hero-badge">Trending Now</div>
              <h2 className="hero-title">{featuredEvent.title}</h2>
              <p className="hero-description">{featuredEvent.description}</p>
              <div className="hero-buttons">
                <button className="hero-btn primary-btn">
                  ‚ñ∂ Book Tickets
                </button>
                <button className="hero-btn secondary-btn">
                  Trailer
                </button>
              </div>
            </div>

            {/* Carousel Indicators */}
            <div className="carousel-indicators">
              {events.slice(0, Math.min(3, events.length)).map((_, idx) => (
                <button
                  key={idx}
                  className={`indicator ${idx === currentSlide % events.length ? 'active' : ''}`}
                  onClick={() => setCurrentSlide(idx)}
                ></button>
              ))}
            </div>
          </div>
        </section>
      )}

      {/* GENRE FILTERS */}
      <section className="genre-section">
        <div className="genre-chips">
          {genres.map((genre) => (
            <button
              key={genre}
              className={`genre-chip ${selectedGenre === genre ? 'active' : ''}`}
              onClick={() => setSelectedGenre(genre)}
            >
              {genre}
            </button>
          ))}
        </div>
      </section>

      {/* NOW SHOWING */}
      <section className="now-showing">
        <div className="section-header">
          <h3>Now Showing</h3>
          <button className="view-all-btn">View All</button>
        </div>

        {events.length === 0 ? (
          <div className="no-events">üì≠ No events available. Check your backend!</div>
        ) : (
          <div className="events-grid">
            {events.map((event) => (
              <EventCard key={event._id} event={event} onBookingSuccess={fetchEvents} />
            ))}
          </div>
        )}
      </section>

      {/* REWARDS BANNER */}
      <section className="rewards-banner">
        <div className="rewards-content">
          <div className="rewards-text">
            <h4>CINEGOLD REWARDS</h4>
            <p>Get 20% off on your first ticket booking with Gold Membership. Join the club now.</p>
          </div>
          <button className="join-btn">Join Membership</button>
        </div>
      </section>

      {/* REFRESH BUTTON */}
      <button 
        onClick={fetchEvents}
        className="refresh-fab"
        title="Refresh events"
      >
        üîÑ
      </button>
    </div>
  );
};

export default Events;
