import React, { useState } from "react";
import API from "../services/api";
import loadRazorpay from "../services/loadRazorpay";
import "../styles/EventCard.css";

const EventCard = ({ event }) => {
  const [seats, setSeats] = useState(1);

  const handleBooking = async () => {
    const res = await loadRazorpay();
    if (!res) {
      alert("Razorpay SDK failed to load");
      return;
    }

    try {
      // 1Ô∏è‚É£ Create order from backend
      const orderRes = await API.post("/payment/create-order", {
        eventId: event._id,
        seatsBooked: seats
      });

      const { id: order_id, amount, currency } = orderRes.data;

      // 2Ô∏è‚É£ Open Razorpay checkout
      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount,
        currency,
        name: "Ticket Booking",
        description: event.title,
        order_id,

        handler: async function (response) {
          // 3Ô∏è‚É£ Verify payment on backend
          try {
            const verifyRes = await API.post("/payment/verify", {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
              eventId: event._id,
              seatsBooked: seats
            });

            alert("‚úÖ Booking confirmed! Order ID: " + verifyRes.data.booking._id);
          } catch (verifyError) {
            console.error("Verification error:", verifyError);
            alert("‚ùå Payment verification failed: " + (verifyError.response?.data?.message || verifyError.message));
          }
        },

        theme: {
          color: "#667eea"
        }
      };

      const paymentObject = new window.Razorpay(options);
      paymentObject.open();

    } catch (error) {
      console.error("Payment error:", error);
      alert("‚ùå " + (error.response?.data?.message || error.message || "Payment failed"));
    }
  };

  const seatsLeft = event.availableSeats;
  const isLowSeats = seatsLeft < 5;

  return (
    <div className="event-card">
      <h3>{event.title}</h3>
      <p>{event.description}</p>
      
      <div className="event-price">‚Çπ{event.price.toLocaleString()}</div>
      
      <div className="event-seats">
        <span className={`seats-info ${isLowSeats ? 'low' : ''}`}>
          üé´ {seatsLeft} {seatsLeft === 1 ? 'seat' : 'seats'} left
        </span>
      </div>

      <div className="booking-section">
        <input
          type="number"
          min="1"
          max={event.availableSeats}
          value={seats}
          onChange={(e) => setSeats(Number(e.target.value))}
          className="seat-input"
        />
        <button 
          onClick={handleBooking}
          disabled={event.availableSeats === 0}
          className="book-btn"
        >
          {event.availableSeats === 0 ? 'Sold Out' : 'Book Now'}
        </button>
      </div>
    </div>
  );
};

export default EventCard;
