import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../services/api";
import EventCard from "../components/EventCard";
import "../styles/Events.css";

const Events = () => {
  const { category } = useParams();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchEvents = () => {
    setLoading(true);
    API.get("/events")
      .then((res) => {
        console.log("Events fetched:", res.data);

        // Filter by category if specified
        let filteredEvents = res.data;
        if (category) {
          filteredEvents = res.data.filter(event =>
            event.category?.toLowerCase() === category.toLowerCase()
          );
        }

        setEvents(filteredEvents);
        setError(null);
      })
      .catch((err) => {
        console.error("Error fetching events:", err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchEvents();
  }, [category]);

  const getCategoryTitle = () => {
    if (!category) return "All Events";
    return category.charAt(0).toUpperCase() + category.slice(1) + " Tickets";
  };

  if (loading) return <div className="loading-text">‚è≥ Loading events...</div>;
  if (error) return <div className="error-text">‚ùå Error: {error}</div>;
  if (events.length === 0) return <div className="no-events">üì≠ No {category || ''} events available. Check your backend!</div>;

  return (
    <div className="events-container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h2>üé¨ {getCategoryTitle()}</h2>
        <button
          onClick={fetchEvents}
          style={{
            padding: '10px 20px',
            background: '#667eea',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '0.9rem',
            transition: 'all 0.3s ease'
          }}
          onMouseEnter={(e) => {
            e.target.style.transform = 'translateY(-2px)';
            e.target.style.boxShadow = '0 8px 20px rgba(102, 126, 234, 0.3)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = 'none';
          }}
        >
          üîÑ Refresh
        </button>
      </div>
      <div className="events-grid">
        {events.map((event) => (
          <EventCard key={event._id} event={event} onBookingSuccess={fetchEvents} />
        ))}
      </div>
    </div>
  );
};

export default Events;
