import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Home.css";

const Home = () => {
    const navigate = useNavigate();

    const categories = [
        {
            id: "movies",
            title: "Movie Tickets",
            icon: "üé¨",
            description: "Book tickets for the latest movies and shows",
            color: "#e74c3c",
            path: "/events/movies"
        },
        {
            id: "trains",
            title: "Train Tickets",
            icon: "üöÇ",
            description: "Reserve your train journey tickets",
            color: "#3498db",
            path: "/events/trains"
        },
        {
            id: "flights",
            title: "Flight Tickets",
            icon: "‚úàÔ∏è",
            description: "Book domestic and international flights",
            color: "#9b59b6",
            path: "/events/flights"
        }
    ];

    return (
        <div className="home-container">
            <div className="home-header">
                <h1>Welcome to Ticket Booking System</h1>
                <p>What would you like to book today?</p>
            </div>

            <div className="categories-grid">
                {categories.map((category) => (
                    <div
                        key={category.id}
                        className="category-card"
                        onClick={() => navigate(category.path)}
                        style={{ "--category-color": category.color }}
                    >
                        <div className="category-icon">{category.icon}</div>
                        <h2>{category.title}</h2>
                        <p>{category.description}</p>
                        <button className="category-btn">
                            Browse {category.title} ‚Üí
                        </button>
                    </div>
                ))}
            </div>

            <div className="home-features">
                <div className="feature-item">
                    <span className="feature-icon">‚ö°</span>
                    <h3>Instant Booking</h3>
                    <p>Quick and easy ticket booking process</p>
                </div>
                <div className="feature-item">
                    <span className="feature-icon">üîí</span>
                    <h3>Secure Payment</h3>
                    <p>Safe and encrypted transactions</p>
                </div>
                <div className="feature-item">
                    <span className="feature-icon">üì±</span>
                    <h3>Digital Tickets</h3>
                    <p>Get your tickets instantly on your device</p>
                </div>
            </div>
        </div>
    );
};

export default Home;
