import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import "../styles/Auth.css";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");
    setIsLoading(true);
    
    try {
      const res = await API.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      setMessage("‚úÖ Login successful! Redirecting...");
      setIsLoggedIn(true);
      setForm({ email: "", password: "" });
      setTimeout(() => navigate("/events"), 1500);
    } catch (err) {
      const errorMsg = err.response?.data?.message || "Login failed";
      setError("‚ùå " + errorMsg);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setForm({ email: "", password: "" });
    setMessage("");
    setError("");
  };

  if (isLoggedIn) {
    return (
      <div className="auth-container">
        <div className="auth-hero">
          <div className="hero-badge">CINEGOLD</div>
          <h1 className="hero-title">Welcome Back!</h1>
          <p className="hero-subtitle">You're all set to book amazing movies</p>
        </div>

        <div className="auth-form-wrapper">
          <div className="auth-card">
            <div className="success-state">
              <div className="success-icon">‚úÖ</div>
              <h3>You're Logged In</h3>
              <p>Enjoy browsing and booking your favorite movies on CineGold</p>
              <button type="button" className="submit-btn logout-btn" onClick={handleLogout}>
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-container">
      <div className="auth-hero">
        <div className="hero-badge">CINEGOLD</div>
        <h1 className="hero-title">Welcome Back to <span className="gold-text">CineGold</span></h1>
        <p className="hero-subtitle">Sign in to your account and start booking premium movie tickets</p>
        <div className="hero-features">
          <div className="feature">
            <span className="feature-icon">üéüÔ∏è</span>
            <p>Book Tickets</p>
          </div>
          <div className="feature">
            <span className="feature-icon">üèÉ</span>
            <p>Quick Checkout</p>
          </div>
          <div className="feature">
            <span className="feature-icon">‚≠ê</span>
            <p>Loyalty Points</p>
          </div>
        </div>
      </div>

      <div className="auth-form-wrapper">
        <div className="auth-card">
          <div className="form-header">
            <h2>Sign In</h2>
            <p className="form-subtitle">Enter your credentials</p>
          </div>
          
          {message && <div className="success-message">{message}</div>}
          {error && <div className="error-message">{error}</div>}

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email Address</label>
              <input
                name="email"
                type="email"
                placeholder="your@email.com"
                value={form.email}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                name="password"
                type="password"
                placeholder="Your password"
                value={form.password}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>

            <button type="submit" className="submit-btn" disabled={isLoading}>
              {isLoading ? "Signing In..." : "Sign In"}
            </button>
          </form>

          <div className="form-footer">
            <p>Don't have an account? <a href="/register" className="form-link">Create one now</a></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
