import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import "../styles/Auth.css";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");
    setIsLoading(true);
    
    try {
      const res = await API.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      setMessage("‚úÖ Login successful! Redirecting...");
      setIsLoggedIn(true);
      setForm({ email: "", password: "" });
      setTimeout(() => navigate("/events"), 1500);
    } catch (err) {
      const errorMsg = err.response?.data?.message || "Login failed";
      setError("‚ùå " + errorMsg);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setForm({ email: "", password: "" });
    setMessage("");
    setError("");
  };

  if (isLoggedIn) {
    return (
      <div className="auth-container">
        <div className="auth-hero">
          <div className="hero-badge">CINEGOLD</div>
          <h1 className="hero-title">Welcome Back!</h1>
          <p className="hero-subtitle">You're all set to book amazing movies</p>
        </div>

        <div className="auth-form-wrapper">
          <div className="auth-card">
            <div className="success-state">
              <div className="success-icon">‚úÖ</div>
              <h3>You're Logged In</h3>
              <p>Enjoy browsing and booking your favorite movies on CineGold</p>
              <button type="button" className="submit-btn logout-btn" onClick={handleLogout}>
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-container login-container">
      <div className="auth-form-wrapper login-wrapper">
        <div className="auth-card login-card">
          <div className="login-header-content">
            <div className="hero-badge">CINEGOLD</div>
            <h2>Sign In</h2>
            <p>Access your premium cinema experience</p>
          </div>

          {message && <div className="success-message">{message}</div>}
          {error && <div className="error-message">{error}</div>}

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email Address</label>
              <input
                name="email"
                type="email"
                placeholder="e.g. alexander@executive.com"
                value={form.email}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>

            <div className="form-group">
              <div className="password-header">
                <label>Password</label>
                <a href="#forgot" className="forgot-password">Forgot Password?</a>
              </div>
              <input
                name="password"
                type="password"
                placeholder="Enter your password"
                value={form.password}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>

            <button type="submit" className="submit-btn sign-in-btn" disabled={isLoading}>
              {isLoading ? "Signing In..." : "Sign In"}
            </button>
          </form>

          <div className="oauth-divider">
            <span>OR CONTINUE WITH</span>
          </div>

          <div className="oauth-buttons">
            <button className="oauth-btn google-btn" type="button">
              <span className="oauth-icon">G</span>
              Google
            </button>
            <button className="oauth-btn apple-btn" type="button">
              <span className="oauth-icon">üçé</span>
              Apple
            </button>
          </div>

          <div className="form-footer login-footer">
            <p>Don't have an account? <a href="/register" className="form-link">Join the Club</a></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
