import React, { useState } from "react";
import API from "../services/api";
import "../styles/EventCard.css";

const EventCard = ({ event, onBookingSuccess }) => {
  const [seats, setSeats] = useState(1);
  const [isBooking, setIsBooking] = useState(false);

  const handleBooking = async () => {
    setIsBooking(true);
    try {
      // Direct booking without Razorpay
      const res = await API.post("/payment/create-order", {
        eventId: event._id,
        seatsBooked: seats
      });

      if (res.data.success) {
        alert("‚úÖ " + res.data.message + "\nBooking ID: " + res.data.booking._id);
        setSeats(1);
        
        // Refresh events to show updated seat count
        if (onBookingSuccess) {
          setTimeout(() => onBookingSuccess(), 1000);
        } else {
          // Fallback: reload page after 1.5 seconds
          setTimeout(() => window.location.reload(), 1500);
        }
      }
    } catch (error) {
      console.error("Booking error:", error);
      alert("‚ùå " + (error.response?.data?.message || error.message || "Booking failed"));
    } finally {
      setIsBooking(false);
    }
  };

  const seatsLeft = event.availableSeats;
  const isLowSeats = seatsLeft < 5;

  return (
    <div className="event-card">
      <h3>{event.title}</h3>
      <p>{event.description}</p>
      
      <div className="event-price">‚Çπ{event.price.toLocaleString()}</div>
      
      <div className="event-seats">
        <span className={`seats-info ${isLowSeats ? 'low' : ''}`}>
          üé´ {seatsLeft} {seatsLeft === 1 ? 'seat' : 'seats'} left
        </span>
      </div>

      <div className="booking-section">
        <input
          type="number"
          min="1"
          max={event.availableSeats}
          value={seats}
          onChange={(e) => setSeats(Number(e.target.value))}
          className="seat-input"
          disabled={isBooking}
        />
        <button 
          onClick={handleBooking}
          disabled={event.availableSeats === 0 || isBooking}
          className="book-btn"
        >
          {isBooking ? 'Booking...' : (event.availableSeats === 0 ? 'Sold Out' : 'Book Now')}
        </button>
      </div>
    </div>
  );
};

export default EventCard;
