import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import "../styles/Home.css";

const Home = () => {
    const navigate = useNavigate();
    const [userName, setUserName] = useState("User");
    const [searchQuery, setSearchQuery] = useState("");
    const [events, setEvents] = useState([]);

    useEffect(() => {
        // Get user name from token or session
        const token = sessionStorage.getItem("token");
        if (token) {
            try {
                const payload = JSON.parse(atob(token.split('.')[1]));
                // You might want to fetch user details from API
                setUserName("Alex Johnson"); // Placeholder
            } catch (e) {
                console.error("Error parsing token:", e);
            }
        }

        // Fetch events for featured sections
        API.get("/events")
            .then((res) => setEvents(res.data))
            .catch((err) => console.error("Error fetching events:", err));
    }, []);

    const categories = [
        { id: "movies", title: "Movies", icon: "üé¨", color: "#e91e63", path: "/events/movies" },
        { id: "buses", title: "Buses", icon: "üöå", color: "#ff9800", path: "/events/trains" },
        { id: "flights", title: "Flights", icon: "‚úàÔ∏è", color: "#2196f3", path: "/events/flights" },
        { id: "events", title: "Events", icon: "üé´", color: "#9c27b0", path: "/events" }
    ];

    const getGreeting = () => {
        const hour = new Date().getHours();
        if (hour < 12) return "Good morning";
        if (hour < 18) return "Good afternoon";
        return "Good evening";
    };

    const movieEvents = events.filter(e => e.category?.toLowerCase() === 'movies').slice(0, 3);
    const busEvents = events.filter(e => e.category?.toLowerCase() === 'trains').slice(0, 2);
    const flightEvents = events.filter(e => e.category?.toLowerCase() === 'flights').slice(0, 1);

    return (
        <div className="home-page">
            {/* Header */}
            <div className="home-header-section">
                <div className="greeting">
                    <div className="user-avatar">üë§</div>
                    <div>
                        <div className="greeting-text">{getGreeting()},</div>
                        <div className="user-name">{userName}</div>
                    </div>
                </div>
                <button className="notification-btn">üîî</button>
            </div>

            {/* Search Bar */}
            <div className="search-section">
                <div className="search-bar">
                    <span className="search-icon">üîç</span>
                    <input
                        type="text"
                        placeholder="Where to next?"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                    <button className="filter-btn">‚ò∞</button>
                </div>
            </div>

            {/* Categories */}
            <div className="categories-section">
                {categories.map((category) => (
                    <div
                        key={category.id}
                        className="category-item"
                        onClick={() => navigate(category.path)}
                        style={{ "--category-color": category.color }}
                    >
                        <div className="category-icon-box">{category.icon}</div>
                        <div className="category-label">{category.title}</div>
                    </div>
                ))}
            </div>

            {/* Popular Movies */}
            <div className="section">
                <div className="section-header">
                    <h2>Popular Movies</h2>
                    <button className="see-all-btn" onClick={() => navigate("/events/movies")}>See All</button>
                </div>
                <div className="movies-grid">
                    {movieEvents.length > 0 ? (
                        movieEvents.map((event) => (
                            <div key={event._id} className="movie-card" onClick={() => navigate("/events/movies")}>
                                <div className="movie-poster">
                                    <div className="movie-rating">‚≠ê {(Math.random() * 2 + 7).toFixed(1)}</div>
                                    <div className="movie-placeholder">{event.title.substring(0, 1)}</div>
                                </div>
                                <div className="movie-title">{event.title}</div>
                                <div className="movie-genre">‚Çπ{event.price} ‚Ä¢ {new Date(event.date).toLocaleDateString()}</div>
                            </div>
                        ))
                    ) : (
                        <div className="no-content">No movies available</div>
                    )}
                </div>
            </div>

            {/* Trending Bus Routes */}
            <div className="section">
                <div className="section-header">
                    <h2>Trending Bus Routes</h2>
                    <button className="more-details-btn" onClick={() => navigate("/events/trains")}>More Details</button>
                </div>
                <div className="routes-list">
                    {busEvents.length > 0 ? (
                        busEvents.map((event) => (
                            <div key={event._id} className="route-card">
                                <div className="route-icon">üöå</div>
                                <div className="route-info">
                                    <div className="route-name">{event.title}</div>
                                    <div className="route-time">Next: {new Date(event.date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</div>
                                </div>
                                <button className="book-route-btn">‚Çπ{event.price}</button>
                            </div>
                        ))
                    ) : (
                        <div className="no-content">No bus routes available</div>
                    )}
                </div>
            </div>

            {/* Flight Deals */}
            <div className="section">
                <div className="section-header">
                    <h2>Flight Deals</h2>
                    <button className="explore-btn" onClick={() => navigate("/events/flights")}>Explore</button>
                </div>
                {flightEvents.length > 0 ? (
                    <div className="flight-deal-card">
                        <div className="flight-deal-image">
                            <div className="flight-placeholder">‚úàÔ∏è</div>
                        </div>
                        <div className="flight-deal-content">
